    $option_name = "insurify_reorder_email_for_customer"; 
    $variable = array("{{customer.name}}","{{products.claimed}}"); 
}elseif( sanitize_text_field($_REQUEST["id"]) == 5){
    $option_name = "insurify_refund_email_for_customer"; 
    $variable = array("{{customer.name}}","{{products.claimed}}"); 
}else{
    $option_name = "insurify_other_email_for_admin"; 
    $variable = array("{{customer.name}}","{{products.claimed}}");
