    /* Save Email Template data into database start */
    jQuery(document).on('click', '.saveDesign', function(e) {
        e.preventDefault();
        unlayer.exportHtml(function(data) { 
            var drpdon = jQuery("#email-template-status").val(); 
            jQuery.ajax({
                url : admin_ecomsurance_ajax_js.ajaxurl,
                type : 'post',
                data : {
                    action : 'saveemailtemplatedesign',
                    json : JSON.stringify(data.design),
                    html : data.html,
                    variable : '<?php echo json_encode($variable); ?>',
                    drpdon : drpdon,
                    optionname : "<?php echo esc_html($option_name); ?>"
                },
                success : function( response ) {
                    location.reload();
                }
            }); 
        });
    });
    /* Save Email Template data into database End */
</script>